import React, { useEffect, useState } from 'react';
import './YourSurveys.css';
import { getSurveys } from '../../firebaseConfig';
import { useAuth } from '../../contexts/AuthContext'
import SurveyCard from '../SurveyCard/SurveyCard'

function YourSurveys(props:any) {
  const { currentUser } = useAuth();
  const surveys:Array<firebase.firestore.DocumentData> = [];

  useEffect(() => {
    getSurveys(currentUser.email).then((collection) => {
      collection.forEach(doc => {
        surveys.push(doc.data())
      })
    }).catch((error) => {
      console.error(error);
    });
  }, [])

  // console.log(surveys);

  return (
    <div className="surveys-container">
      {surveys.map((survey) => {
        return <SurveyCard survey={survey}/>
      })}
    </div>
  )
};

export default YourSurveys
