import React from 'react';
import './EditProfile.css';
import BaseButton from '../BaseButton/BaseButton'
import BaseInputField from '../BaseInputField/BaseInputField';
import BaseSelectOne from '../BaseSelectOne/BaseSelectOne';
import BaseSelectMultiple from '../BaseSelectMultiple/BaseSelectMultiple';
import SecondaryButton from "../../components/SecondaryButton/SecondaryButton";
import { pushData } from '../../firebaseConfig';
import { useAuth } from '../../contexts/AuthContext'
import { toast } from '../../toast'


function EditProfile(props: any) {
  const { toggleState } = props;
  const { currentUser } = useAuth();
  let canSubmit = true;
  let surveyData: any = {};

  function postData() {
    if (currentUser) {
      calPolyStatus != '' ? surveyData.calPolyStatus = calPolyStatus : canSubmit = false;
      preferFeel != '' ? surveyData.thermalPreference = preferFeel : canSubmit = false;
      airCirculation != '' ? surveyData.airCirculationSensation = airCirculation : canSubmit = false;
      humid != '' ? surveyData.humiditySensation = humid : canSubmit = false;
      clothing != '' ? surveyData.clothing = clothing : canSubmit = false;
      activities != [] ? surveyData.recentAction = activities : canSubmit = false;
      if(canSubmit){
        try {
          pushData(surveyData, currentUser.email);
          toast(`Thank you! Survey submitted successfully.`);
        }
        catch(error) {
          toast(`Error logging out: ${error}`);
        }
        toggleState();
      }
      else {
        toast('Please answer all of the non-optional questions before submitting.');
      }
    }
  }

  const calPolyStatusList = [
    {id: 1, val: 'Cold'},
    {id: 2, val: 'Cool'},
    {id: 3, val: 'Slightly Cool'},
    {id: 4, val: 'Neutral: Neither Cool or Warm'},
    {id: 5, val: 'Slightly Warm'},
    {id: 6, val: 'Warm'},
    {id: 7, val: 'Hot'},
  ]
  const [calPolyStatus, setCalPolyStatus] = React.useState('');

  const preferFeelList = [
    {id: 1, val: 'Cooler'},
    {id: 2, val: 'No Change'},
    {id: 3, val: 'Warmer'},
  ]
  const [preferFeel, setPreferFeel] = React.useState('');

  const airCirculationList = [
    {id: 1, val: 'Cooler'},
    {id: 2, val: 'No Change'},
    {id: 3, val: 'Warmer'},
  ]
  const [airCirculation, setAirCirculation] = React.useState('');

  const humidList = [
    {id: 1, val: 'Too Dry'},
    {id: 2, val: 'Slightly Dry'},
    {id: 3, val: 'Just Right'},
    {id: 4, val: 'Slightly Humid'},
    {id: 5, val: 'Too Humid'},
  ]
  const [humid, setHumid] = React.useState('');

  const clothingList = [
    {id: 1, val: 'Minimal [shorts, shirt]'},
    {id: 2, val: 'Average [pants, shirt]'},
    {id: 3, val: 'Heavy [pants, jacket/sweater/sweatshirt]'},
  ]
  const [clothing, setClothing] = React.useState('');

  const activitiesList = [
    {id: 1, val: 'Sitting'},
    {id: 2, val: 'Standing'},
    {id: 3, val: 'Walking'},
    {id: 4, val: 'Jogging/Fast Walking'},
    {id: 5, val: 'Running'},
    {id: 6, val: 'Other'},
  ]
  const [activities, setActivities] = React.useState([]);

  return (
    <div className="editprofile-container">
      <div className="top-buttons">
            <SecondaryButton onClick={toggleState}>Back</SecondaryButton>
        </div>
      <h1>Edit Profile</h1>
      <p>What is your status at Cal Poly?</p>
      <BaseSelectOne
        optionsList={calPolyStatusList}
        value={tempInRoom}
        onChange={(e: any) => setCalPolyStatus(e.target.value)}
      />
      <p>What biological sex were you assigned at birth?</p>
      <BaseSelectOne
        optionsList={preferFeelList}
        value={preferFeel}
        onChange={(e: any) => setPreferFeel(e.target.value)}
      />
      <p>What is your gender identification?</p>
      <BaseSelectOne
        optionsList={airCirculationList}
        value={airCirculation}
        onChange={(e: any) => setAirCirculation(e.target.value)}
      />
      <p>What is your ethnicity?</p>
      <BaseSelectOne
        optionsList={humidList}
        value={humid}
        onChange={(e: any) => setHumid(e.target.value)}
      />
      <p>Do you have a disability?</p>
      <BaseSelectOne
        optionsList={clothingList}
        value={clothing}
        onChange={(e: any) => setClothing(e.target.value)}
      />
      <p>What is your age?</p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      <p>What is your familyâ€™s or was your childhood economic situation?</p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      <p>What climate did you grow up in [ages 0-10]?</p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      <p>Indoor Room Condition Thermal Comfort Preference?</p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      <p>Do you feel that one of the questions did not have a suitable answer to represent you? If so please explain. <em>*OPTIONAL*</em></p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      <div className='editprofile-submit'>
        <BaseButton width={'300px'} onClick={postData}>Submit</BaseButton>
      </div>
    </div>
  )
};

export default EditProfile
