import React from 'react';
import './EditProfile.css';
import BaseButton from '../BaseButton/BaseButton'
import BaseInputField from '../BaseInputField/BaseInputField';
import BaseSelectOne from '../BaseSelectOne/BaseSelectOne';
import BaseSelectMultiple from '../BaseSelectMultiple/BaseSelectMultiple';
import SecondaryButton from "../../components/SecondaryButton/SecondaryButton";
import { pushData } from '../../firebaseConfig';
import { useAuth } from '../../contexts/AuthContext'
import { toast } from '../../toast'


function EditProfile(props: any) {
  const { toggleState } = props;
  const { currentUser } = useAuth();
  let canSubmit = true;
  let surveyData: any = {};

  function postData() {
    if (currentUser) {
      calPolyStatus != '' ? surveyData.calPolyStatus = calPolyStatus : canSubmit = false;
      biologicalSex != '' ? surveyData.biologicalSex = biologicalSex : canSubmit = false;
      genderIdentification != '' ? surveyData.genderIdentification = genderIdentification : canSubmit = false;
      ethnicity != '' ? surveyData.ethnicity = ethnicity : canSubmit = false;
      disability != '' ? surveyData.disability = disability : canSubmit = false;
      age != '' ? surveyData.age = age : canSubmit = false;
      if(canSubmit){
        try {
          pushData(surveyData, currentUser.email);
          toast(`Thank you! Survey submitted successfully.`);
        }
        catch(error) {
          toast(`Error logging out: ${error}`);
        }
        toggleState();
      }
      else {
        toast('Please answer all of the non-optional questions before submitting.');
      }
    }
  }

  const calPolyStatusList = [
    {id: 1, val: 'Cold'},
    {id: 2, val: 'Cool'},
    {id: 3, val: 'Slightly Cool'},
    {id: 4, val: 'Neutral: Neither Cool or Warm'},
    {id: 5, val: 'Slightly Warm'},
    {id: 6, val: 'Warm'},
    {id: 7, val: 'Hot'},
  ]
  const [calPolyStatus, setCalPolyStatus] = React.useState('');

  const biologicalSexList = [
    {id: 1, val: 'Cooler'},
    {id: 2, val: 'No Change'},
    {id: 3, val: 'Warmer'},
  ]
  const [biologicalSex, setBiologicalSex] = React.useState('');

  const genderIdentificationList = [
    {id: 1, val: 'Cooler'},
    {id: 2, val: 'No Change'},
    {id: 3, val: 'Warmer'},
  ]
  const [genderIdentification, setGenderIdentification] = React.useState('');

  const ethnicityList = [
    {id: 1, val: 'Too Dry'},
    {id: 2, val: 'Slightly Dry'},
    {id: 3, val: 'Just Right'},
    {id: 4, val: 'Slightly Humid'},
    {id: 5, val: 'Too Humid'},
  ]
  const [ethnicity, setEthnicity] = React.useState('');

  const disabilityList = [
    {id: 1, val: 'Minimal [shorts, shirt]'},
    {id: 2, val: 'Average [pants, shirt]'},
    {id: 3, val: 'Heavy [pants, jacket/sweater/sweatshirt]'},
  ]
  const [disability, setDisability] = React.useState('');

  const [age, setAge] = React.useState('');

  const activitiesList = [
    {id: 1, val: 'Sitting'},
    {id: 2, val: 'Standing'},
    {id: 3, val: 'Walking'},
    {id: 4, val: 'Jogging/Fast Walking'},
    {id: 5, val: 'Running'},
    {id: 6, val: 'Other'},
  ]
  const [activities, setActivities] = React.useState([]);

  return (
    <div className="editprofile-container">
      <div className="top-buttons">
            <SecondaryButton onClick={toggleState}>Back</SecondaryButton>
        </div>
      <h1>Edit Profile</h1>
      <p>What is your status at Cal Poly?</p>
      <BaseSelectOne
        optionsList={calPolyStatusList}
        value={calPolyStatus}
        onChange={(e: any) => setCalPolyStatus(e.target.value)}
      />
      <p>What biological sex were you assigned at birth?</p>
      <BaseSelectOne
        optionsList={biologicalSexList}
        value={biologicalSex}
        onChange={(e: any) => setBiologicalSex(e.target.value)}
      />
      <p>What is your gender identification?</p>
      <BaseSelectOne
        optionsList={genderIdentificationList}
        value={genderIdentification}
        onChange={(e: any) => setGenderIdentification(e.target.value)}
      />
      <p>What is your ethnicity?</p>
      <BaseSelectOne
        optionsList={ethnicityList}
        value={ethnicity}
        onChange={(e: any) => setEthnicity(e.target.value)}
      />
      <p>Do you have a disability?</p>
      <BaseSelectOne
        optionsList={disabilityList}
        value={disability}
        onChange={(e: any) => setDisability(e.target.value)}
      />
      <div className="baseinput-div">
        <BaseInputField
          label={'What is your age?'}
          iduser={'room'}
          width={'300px'}
          value={age}
          onChange={(e: any) => setA(e.target.value)}
        />
      </div>
      <p>What is your familyâ€™s or was your childhood economic situation?</p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      <p>What climate did you grow up in [ages 0-10]?</p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      <p>Indoor Room Condition Thermal Comfort Preference?</p>
      <BaseSelectMultiple
        optionsList={activitiesList}
        value={activities}
        onChange={(e: any) => setActivities(e.target.value)}
      />
      {/* <div className="baseinput-div">
        <BaseInputField
          label={'Do you feel that one of the questions did not have a suitable answer to represent you? If so please explain. <em>*OPTIONAL*</em>'}
          iduser={'room'}
          width={'300px'}
          value={roomNumber}
          onChange={(e: any) => setRoomNumber(e.target.value)}
        />
      </div> */}
      <div className='editprofile-submit'>
        <BaseButton width={'300px'} onClick={postData}>Submit</BaseButton>
      </div>
    </div>
  )
};

export default EditProfile
